INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH1'), 'Trường STU nhận hồ sơ xét tuyển sớm từ ngày 16/3 đến 23/6/2023.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH2'), 'Có 4 phương thức xét tuyển: xét tuyển bằng học bạ 3 học kỳ, xét tuyển bằng học bạ 5 học kỳ, xét tuyển bằng học bạ lớp 12 và xét tuyển bằng điểm thi Đánh giá năng lực do ĐHQG TP.HCM tổ chức.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH3'), 'Điểm xét tuyển = Điểm TB HK1 lớp 11 + Điểm TB HK2 lớp 11 + Điểm TB HK1 lớp 12.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH4'), 'Thời gian nhận hồ sơ xét tuyển sớm được chia thành 3 đợt: Đợt 1: từ 16/3 đến 21/4/2023 => Thông báo kết quả ngày 28/4/2023, Đợt 2: từ 24/4 đến 26/5/2023 => Thông báo kết quả ngày 02/6/2023, Đợt 3: từ 29/5 đến 23/6/2023 => Thông báo kết quả ngày 30/6/2023.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH5'), 'Hồ sơ đăng ký xét tuyển gồm: Phiếu đăng ký xét tuyển theo mẫu của STU. Chứng minh nhân dân hoặc Thẻ căn cước công dân (bản sao có công chứng). Chứng nhận ưu tiên khu vực và đối tượng tuyển sinh (nếu có). Bằng tốt nghiệp THPT hoặc Giấy chứng nhận tốt nghiệp THPT 2023 hoặc tương đương (bản sao có công chứng). Giấy tờ minh chứng cho kết quả học tập/kỳ thi theo từng phương thức xét tuyển.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH6'), 'Bạn nên liên hệ với Văn phòng Tư vấn Tuyển sinh của STU hoặc truy cập vào địa chỉ trang web của trường để biết thông tin chi tiết.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH7'), 'Trong đợt xét tuyển sớm, không thu lệ phí ');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH8'), 'Có 3 cách nộp hồ sơ xét tuyển: Thí sinh có thể nộp hồ sơ trực tiếp tại Văn phòng Tư vấn Tuyển sinh - Trường Đại học Công nghệ Sài Gòn, địa chỉ: 180 Cao Lỗ, Phường 4, Quận 8, TP. Hồ Chí Minh. Gửi hồ sơ qua đường bưu điện (chuyển phát nhanh) về Văn phòng Tư vấn Tuyển sinh - Trường Đại học Công nghệ Sài Gòn, địa chỉ: 180 Cao Lỗ, Phường 4, Quận 8, TP. Hồ Chí Minh. Đăng ký xét tuyển trực tuyến tại địa chỉ http://daotao2.stu.edu.vn/.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH9'), 'Bạn có thể liên hệ và được tư vấn, hỗ trợ từ STU qua các kênh thông tin sau: Địa chỉ: 180 Cao Lỗ, Phường 4, Quận 8, TP. Hồ Chí Minh. Số điện thoại: (028) 505.520 - 115 ; 116 hoặc Hotline: 0902.992.306. Kênh tư vấn trực tuyến: http://stu.edu.vn/vi/269/cau-hoi-tu-van.html .Fanpage STU: https://www.facebook.com/DHCNSG .Zalo: 085.756.3180');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH10'), 'Có, bạn có thể nộp hồ sơ xét tuyển cho nhiều ngành học tại STU. Tuy nhiên, bạn cần lưu ý rằng mỗi ngành có yêu cầu và tổ hợp môn xét tuyển riêng. Vui lòng kiểm tra thông tin chi tiết về ngành và tổ hợp môn xét tuyển trên trang web của trường hoặc liên hệ với Văn phòng Tư vấn Tuyển sinh để được tư vấn thêm.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH11'), 'Nếu bạn đã tốt nghiệp THPT trước năm 2023, bạn có thể nộp giấy chứng nhận tốt nghiệp cũ cho hồ sơ xét tuyển. Tuy nhiên, vui lòng lưu ý rằng giấy chứng nhận tốt nghiệp phải có bản sao công chứng.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH12'), 'Nếu bạn không có ưu tiên khu vực hoặc đối tượng tuyển sinh, bạn không cần nộp chứng nhận ưu tiên. Tuy nhiên, bạn vẫn cần nộp các giấy tờ bắt buộc khác như chứng minh nhân dân hoặc thẻ căn cước công dân, bằng tốt nghiệp THPT hoặc giấy chứng nhận tốt nghiệp tương đương.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH13'), 'Không, nếu bạn không tham gia kỳ thi Đánh giá năng lực do ĐHQG TP.HCM tổ chức, bạn không thể xét tuyển bằng phương thức này. Bạn có thể lựa chọn một trong ba phương thức xét tuyển khác: học bạ 3 học kỳ, học bạ 5 học kỳ hoặc học bạ lớp 12.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH14'), 'Điểm TB cả năm lớp 10 được tính bằng cách lấy tổng điểm trung bình cả năm của tất cả các môn học trong lớp 10 chia cho số môn học.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH15'), 'Điểm TB 3 môn theo tổ hợp môn được tính bằng cách lấy tổng điểm trung bình của 3 môn học trong tổ hợp môn xét tuyển của bạn.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH16'), 'Nếu bạn gặp khó khăn trong quá trình nộp hồ sơ hoặc cần tư vấn thêm, bạn có thể liên hệ với STU qua các kênh sau: Số điện thoại: (028) 505.520 - 115 ; 116 hoặc Hotline: 0902.992.306. Kênh tư vấn trực tuyến: http://stu.edu.vn/vi/269/cau-hoi-tu-van.html . Fanpage STU: https://www.facebook.com/DHCNSG . Zalo: 085.756.3180');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH17'), 'Bạn có thể tải phiếu đăng ký xét tuyển theo mẫu của STU trên trang web của trường. Vào trang web chính thức của Trường Đại học Công nghệ Sài Gòn (STU) và tìm mục "Tuyển sinh" hoặc "Đăng ký xét tuyển" để tìm và tải về phiếu đăng ký.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH18'), 'Trong trường hợp bạn không nhận được thông báo kết quả sau khi đã nộp hồ sơ xét tuyển, bạn nên liên hệ trực tiếp với Văn phòng Tư vấn Tuyển sinh - Trường Đại học Công nghệ Sài Gòn. Sử dụng số điện thoại hoặc địa chỉ email của trường để liên hệ và yêu cầu thông tin về tình trạng xét tuyển của bạn.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH19'), 'Thông tin về việc yêu cầu chuyển ngành sau khi được nhập học tại STU có thể khác nhau tùy theo quy định của trường. Bạn nên liên hệ trực tiếp với Phòng Đào tạo hoặc Văn phòng Tư vấn Tuyển sinh của trường để được cung cấp thông tin chi tiết về quy trình và điều kiện chuyển ngành.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH20'), 'Trường Đại học Công nghệ Sài Gòn (STU) có thể chấp nhận hồ sơ xét tuyển từ thí sinh tốt nghiệp trung học quốc tế. Tuy nhiên, quy trình và yêu cầu cụ thể có thể khác nhau tùy theo từng trường hợp. Bạn nên liên hệ trực tiếp với Văn phòng Tư vấn Tuyển sinh để biết thông tin chi tiết và yêu cầu xét tuyển đối với thí sinh tốt nghiệp trung học quốc tế.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH21'), 'Trường Đại học Công nghệ Sài Gòn (STU) thường cung cấp chương trình học bổng cho sinh viên xuất sắc. Thông tin về các chương trình học bổng và tiêu chí xét học bổng có thể được tìm thấy trên trang web của trường hoặc liên hệ trực tiếp với Phòng Đào tạo hoặc Văn phòng Tư vấn Tuyển sinh để biết thông tin chi tiết và yêu cầu đối với học bổng.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH22'), 'Thông tin về khả năng nộp hồ sơ xét tuyển sau thời hạn chót của đợt xét tuyển sớm cần phải được xác nhận trực tiếp từ Văn phòng Tư vấn Tuyển sinh của trường. Liên hệ với trường qua số điện thoại hoặc địa chỉ email để biết thêm chi tiết và được tư vấn đúng thông tin.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH23'), 'Nếu bạn đã tốt nghiệp THPT trước năm 2023, bạn cần nộp bảng điểm học bạ theo phương thức xét tuyển mà bạn chọn (học bạ 3 học kỳ hoặc học bạ 5 học kỳ). Vui lòng xem lại yêu cầu và hướng dẫn xét tuyển trên trang web của trường hoặc liên hệ với Văn phòng Tư vấn Tuyển sinh để được hỗ trợ.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH24'), 'Thông tin chi tiết về hệ thống đào tạo, chương trình học và các ngành học tại STU có thể được tìm thấy trên trang web chính thức của trường. Truy cập vào trang web của STU và tìm mục "Hệ thống đào tạo" hoặc "Chương trình đào tạo" để có thông tin chi tiết về các ngành học, môn học, và cấu trúc chương trình.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH25'), 'Thông tin về kết quả xét tuyển sẽ được thông báo trên trang web chính thức của STU. Bạn cũng có thể liên hệ trực tiếp với Văn phòng Tư vấn Tuyển sinh để được thông báo kết quả xét tuyển. Trong hồ sơ xét tuyển, bạn cũng nên cung cấp thông tin liên hệ chính xác để nhận thông báo kết quả.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH26'), 'Sau khi được nhận vào STU, bạn sẽ được hướng dẫn về các thủ tục nhập học cụ thể. Thông tin về thời gian, địa điểm và yêu cầu thủ tục nhập học sẽ được thông báo cho bạn qua email hoặc trang web của trường. Hãy kiên nhẫn chờ đợi và làm theo hướng dẫn của trường để hoàn tất thủ tục nhập học.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH27'), 'STU có thể cung cấp các chương trình hỗ trợ sinh viên ở xa như ký túc xá, dịch vụ hỗ trợ sinh viên và thông tin về các dịch vụ, tiện ích xung quanh khu vực trường. Thông tin chi tiết về chương trình hỗ trợ sinh viên ở xa có thể được tìm thấy trên trang web của trường hoặc liên hệ trực tiếp với Phòng Đào tạo hoặc Văn phòng Tư vấn Tuyển sinh.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH28'), 'Trường chúng tôi có tổng cộng 7 khoa/ 8 ngành học, bao gồm: 1 Khoa Công Nghệ Thông Tin, 2 Khoa Công Nghệ Thực Phẩm, 3 Khoa Kỹ Thuật Công Trình, 4 Khoa Điện – Điện Tử, 5 Khoa Cơ Khí, 6 Khoa Quản Trị Kinh Doanh, 7 Khoa Design Và 8 ngành học có thể đăng ký xét tuyển là 1 Công nghệ Kỹ thuật Cơ - Điện tử, 2 Công nghệ Kỹ thuật Điện - Điện tử, 3 Công nghệ Kỹ thuật Điện tử - Viễn thông, 4 Kỹ thuật Xây dựng, 5 Công nghệ Thông tin, 6 Công nghệ Thực phẩm, 7 Quản trị Kinh doanh, 8 Thiết kế Công nghiệp');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH29'), 'Điểm chuẩn của trường chúng tôi thường thay đổi từ năm này sang năm khác và phụ thuộc vào từng khoa/ngành. Để biết thông tin chi tiết về điểm chuẩn, bạn nên tham khảo trang web của trường hoặc liên hệ với phòng Tuyển sinh.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH30'), 'Chúng tôi có một loạt các câu lạc bộ sinh viên với các hoạt động văn hóa, thể thao và hướng nghiệp. Sinh viên có thể tham gia vào các câu lạc bộ này để gặp gỡ bạn bè mới và phát triển kỹ năng cá nhân.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH31'), 'Nếu bạn là sinh viên quốc tế, bạn có thể liên hệ với phòng Quan hệ Quốc tế hoặc Văn phòng Tuyển sinh Quốc tế để biết thông tin về chương trình học và các yêu cầu đăng ký dành riêng cho sinh viên quốc tế.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH32'), 'Chúng tôi có ký túc xá dành riêng cho sinh viên, cung cấp chỗ ở an ninh và tiện nghi để hỗ trợ sinh viên ở xa.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH33'), 'Bạn có thể tải biểu mẫu "Đơn xin gia hạn thời gian thực hiện đề tài Nghiên cứu khoa học cấp trường" từ trang web và điền đầy đủ thông tin theo yêu cầu. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và xử lý.');
