INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH37'), 'Bạn có thể tải biểu mẫu "Đơn xin giải quyết học vụ" từ trang web và điền đầy đủ thông tin theo yêu cầu. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và giải quyết vấn đề.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH38'), 'Bạn có thể tải biểu mẫu "Mẫu đơn xin chuyển nhóm" từ trang web và điền đầy đủ thông tin theo yêu cầu. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và xử lý yêu cầu chuyển nhóm.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH39'), 'Bạn có thể tải biểu mẫu "Mẫu đơn xin chuyển lớp" từ trang web và điền đầy đủ thông tin theo yêu cầu. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và xử lý yêu cầu chuyển lớp.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH40'), 'Bạn có thể tải biểu mẫu "Mẫu đơn xin xác nhận là sinh viên đang học tại trường" từ trang web và điền đầy đủ thông tin cá nhân theo yêu cầu. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và cung cấp xác nhận.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH41'), 'Để đánh giá kết quả rèn luyện, bạn có thể tải biểu mẫu "Biểu mẫu đánh giá kết quả rèn luyện của học sinh - sinh viên" từ trang web và điền đầy đủ thông tin về các tiêu chí đánh giá. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và lưu trữ.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH42'), 'Để xin miễn giảm học phí cho sinh viên hệ liên thông đại học, bạn có thể tải biểu mẫu "Đơn xét miễn giảm học phí cho sinh viên hệ liên thông đại học" từ trang web và điền đầy đủ thông tin cá nhân và lý do xin miễn giảm học phí. Sau đó, nộp đơn tới cơ quan/phòng ban có thẩm quyền để xem xét và xử lý.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH43'), 'Bạn có thể chuyển khoản tiền học phí đến một trong số 6 tài khoản ngân hàng của Trường Đại học Công nghệ Sài Gòn. Vui lòng xem danh sách các tài khoản và chi nhánh/phòng giao dịch tương ứng trên trang web của trường. Đảm bảo ghi đúng tên tài khoản của trường, số tài khoản và chi nhánh/phòng giao dịch. Trong nội dung chuyển khoản, bạn cần ghi đầy đủ thông tin cá nhân như họ tên sinh viên, MSSV và học kỳ năm học theo phiếu báo "Kết quả đăng ký môn học và học phí" được cấp bởi Phòng Đào tạo Ví dụ: Nguyễn Văn A, MSSV DH190001, học kỳ 2 năm học 2022 – 2023.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH44'), 'Sau khi chuyển khoản học phí, bạn cần liên hệ với Phòng Kế hoạch - Tài chính của trường để nhận biên lại đóng học phí. Mang theo thẻ sinh viên và phiếu báo "Kết quả đăng ký môn học và học phí" để đối chiếu khi cần thiết.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH45'), 'Mức học phí của mỗi sinh viên được xác định dựa trên chương trình học (số tín chỉ) mà sinh viên đăng ký học trong học kỳ. Học phí sẽ được thể hiện trong tài khoản cá nhân của sinh viên sau khi sinh viên hoàn thành đăng ký môn học. Bạn có thể xem thông tin chi tiết về mức học phí và tài khoản cá nhân của mình trên trang web của trường.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH46'), 'Bạn có thể đến trực tiếp Phòng Kế hoạch - Tài chính đóng tiền hoặc Bạn có thể đóng tiền học phí tại các điểm giao dịch của ngân hàng mà trường đã chỉ định. Vui lòng đến điểm giao dịch tương ứng với ngân hàng mà bạn chọn và đảm bảo ghi đầy đủ thông tin tài khoản và tên tài khoản của trường. Sau khi đóng tiền, bạn cần liên hệ với Phòng Kế hoạch - Tài chính để nhận biên lại đóng học phí.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH47'), 'Số tiền chuyển khoản phải đúng theo số tiền ghi trong phiếu báo "Kết quả đăng ký môn học và học phí" do Phòng Đào tạo cấp. Phiếu báo này sẽ cung cấp cho bạn thông tin chi tiết về mức học phí cần đóng.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH48'), 'Sau khi chuyển khoản học phí, khi liên hệ với Phòng Kế hoạch - Tài chính để nhận biên lại đóng học phí, bạn cần mang theo thẻ sinh viên và phiếu báo "Kết quả đăng ký môn học và học phí" để đối chiếu khi cần thiết.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH49'), 'Hướng dẫn đóng học phí thông qua chuyển khoản tiền nhằm hạn chế việc sử dụng tiền mặt và tạo điều kiện thuận tiện cho phụ huynh và sinh viên. Tuy nhiên, nếu bạn không thể thực hiện chuyển khoản, bạn có thể liên hệ với Phòng Kế hoạch - Tài chính để biết về các phương thức khác để đóng học phí, bao gồm đóng bằng tiền mặt tại trường.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH50'), 'Để biết thông tin chi tiết về học phí và các khoản phí khác ngoài học phí (nếu có), bạn nên xem trong tài liệu hướng dẫn đăng ký môn học và học phí được cung cấp bởi Phòng Đào tạo. Nếu bạn cần thông tin cụ thể hơn, hãy liên hệ với Phòng Đào tạo hoặc Phòng Kế hoạch - Tài chính để được tư vấn và giải đáp thắc mắc của bạn.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH51'), 'Đối tượng được xét học bổng khó khăn là sinh viên thuộc đối tượng có hoàn cảnh khó khăn, có điểm rèn luyện và điểm học tập trung bình năm học 2022-2023 từ 6.5 trở lên. Ưu tiên xét học bổng cho những sinh viên chưa được xét miễn giảm học phí hoặc chưa nhận bất kỳ học bổng nào trong năm học 2022-2023.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH52'), 'Hồ sơ xét học bổng bao gồm: Đơn xin cấp học bổng theo mẫu (có thể nhận trực tiếp tại Phòng Công tác Sinh viên hoặc tải tại đường link đã cung cấp trong thông báo). Giấy tờ pháp lý chứng minh hoàn cảnh như giấy xác nhận của địa phương, sổ hộ nghèo, hộ cận nghèo, giấy xác nhận hoàn cảnh khó khăn. Bảng điểm học tập năm học 2022-2023. Điểm rèn luyện năm học 2022-2023 từ loại Khá trở lên. Giấy tờ ưu tiên khác như giấy khen (nếu có).');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH53'), 'Sinh viên nộp hồ sơ trực tiếp tại Phòng Công tác Sinh viên (Phòng A.406) từ ngày 13/02/2023 đến hết ngày 22/02/2023.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH54'), 'Thông báo không đề cập đến giới hạn số lượng học bổng được cấp cho sinh viên. Tuy nhiên, thông thường có một số lượng hạn chế về học bổng được cấp dựa trên nguồn tài chính và tiêu chí xét học bổng.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH55'), 'Thông báo không cung cấp thông tin về giá trị tài chính của học bổng. Để biết thông tin chi tiết về giá trị tài chính và các điều kiện đi kèm, bạn nên liên hệ trực tiếp với Phòng Công tác Sinh viên để được cung cấp thông tin cụ thể.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH56'), 'Sau khi nộp hồ sơ xét học bổng, sinh viên có thể liên hệ với Phòng Công tác Sinh viên để biết thông tin về tiến trình xét duyệt. Thông báo thông tin về kết quả xét học bổng có thể được đăng tải trên trang web hoặc thông qua các kênh thông tin khác của trường.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH57'), 'Thông báo không cung cấp thông tin về điều kiện duy trì học bổng. Để biết thông tin chi tiết về điều kiện duy trì và các yêu cầu khác, bạn nên liên hệ trực tiếp với Phòng Công tác Sinh viên hoặc tham khảo các tài liệu liên quan.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH58'), 'Thông báo không đề cập đến việc nộp hồ sơ xét học bổng sau thời hạn quy định. Tuy nhiên, để tăng cơ hội được xem xét, bạn nên tuân thủ thời gian nộp hồ sơ theo quy định. Nếu bạn có trường hợp đặc biệt và không thể nộp hồ sơ đúng thời hạn, hãy liên hệ trực tiếp với Phòng Công tác Sinh viên để được tư vấn và thông báo chi tiết về trường hợp của bạn.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH59'), 'Thông báo không đề cập đến việc nộp hồ sơ xét học bổng qua email. Để biết cách nộp hồ sơ chính xác, bạn nên tuân thủ hướng dẫn trong thông báo và nộp hồ sơ trực tiếp tại Phòng Công tác Sinh viên theo địa chỉ được cung cấp.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH60'), 'Thông báo không cung cấp thông tin về việc áp dụng học bổng cho các ngành học cụ thể. Để biết thông tin chi tiết về việc áp dụng học bổng cho từng ngành học, bạn nên liên hệ trực tiếp với Phòng Công tác Sinh viên để được cung cấp thông tin cụ thể và chi tiết về các ngành học được áp dụng học bổng.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH61'), 'Thông báo không cung cấp thông tin về số lượng học bổng được cấp trong chương trình. Số lượng học bổng có thể thay đổi tùy thuộc vào số lượng sinh viên đủ điều kiện và số lượng nguồn tài trợ. Để biết thông tin chi tiết về số lượng học bổng và cơ hội nhận học bổng, bạn nên liên hệ trực tiếp với Phòng Công tác Sinh viên để được cung cấp thông tin cụ thể.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH62'), 'Thông báo không đề cập đến thời gian thông báo kết quả xét học bổng. Thông thường, sau khi kết thúc thời gian nộp hồ sơ, ban tổ chức sẽ tiến hành xem xét và xử lý hồ sơ. Kết quả xét học bổng sẽ được thông báo trực tiếp cho sinh viên qua email, thông báo trên trang web của trường hoặc thông qua các phương tiện liên lạc khác. Để biết thời gian thông báo chính xác, bạn nên liên hệ trực tiếp với Phòng Công tác Sinh viên để được cung cấp thông tin cụ thể và cập nhật về kế hoạch thông báo kết quả.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH63'), 'Thông báo không đề cập đến yêu cầu điều kiện tiếp tục nhận học bổng trong các năm học sau. Tuy nhiên, thông thường, các học bổng có yêu cầu điều kiện nhất định để tiếp tục nhận hỗ trợ, như duy trì điểm rèn luyện và điểm học tập ở mức xác định. Để biết thông tin chi tiết về yêu cầu và điều kiện tiếp tục nhận học bổng, bạn nên liên hệ trực tiếp với Phòng Công tác Sinh viên để được cung cấp thông tin cụ thể và chi tiết về các yêu cầu và quy định liên quan.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH64'), 'Chào bạn, hiện tại bạn có thể nộp hồ sơ học bạ trước nhé. Bạn cần chuẩn bị hồ sơ học bạ gốc hoặc bản sao công chứng và nộp tại Văn phòng Tuyển sinh Trường hoặc gửi qua đường bưu điện.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH65'), 'Chào bạn, hiện tại bạn có thể gửi hồ sơ xét tuyển học bạ sớm. Bạn cần chuẩn bị hồ sơ học bạ gốc hoặc bản sao công chứng và nộp tại Văn phòng Tuyển sinh Trường hoặc gửi qua đường bưu điện.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH66'), 'Chào bạn, hiện tại bạn có thể gửi hồ sơ xét tuyển học bạ sớm nhé. Bạn cần chuẩn bị hồ sơ học bạ gốc hoặc bản sao công chứng và nộp tại Văn phòng Tuyển sinh Trường hoặc gửi qua đường bưu điện.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH67'), 'Chào bạn, bạn có thể đăng ký qua xét tuyển các hình thức sau: Đăng ký xét tuyển trực tuyến tại website http://daotao2.stu.edu.vn/ Hoặc nộp trực tiếp tại Văn phòng Tuyển sinh Trường Hoặc gửi qua đường bưu điện.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH68'), 'Chào bạn, hiện tại trường kết thúc xét tuyển sớm. Bạn có thể đăng ký nguyện vọng trên hệ thống của Bộ GD&ĐT từ ngày 10/07 - 30/7.');
