INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH121'), 'Chào bạn, Nếu xác định sẽ nhập học hệ chính quy, trình độ đại học, ngành đã trúng tuyển có điều kiện của Trường Đại học Công nghệ Sài Gòn, thí sinh cần phải thực hiện thêm bước đăng ký xét tuyển trên hệ thống chung của Bộ GD&ĐT: - Thí sinh đăng ký xét tuyển trên Cổng Thông tin tuyển sinh của Bộ GD&ĐT từ ngày 10/07/2023 đến trước 17 giờ 00, ngày 30/07/2023. Khi đăng ký xét tuyển, thí sinh lựa chọn ngành học đã trúng tuyển có điều kiện của Trường Đại học Công nghệ Sài Gòn - Trường Đại học Công nghệ Sài Gòn tải dữ liệu đăng ký xét tuyển từ Hệ thống Tuyển sinh Quốc gia của Bộ GD&ĐT để gọi thí sinh trúng tuyển và nhập học. Kết quả trúng tuyển được công bố trước 17 giờ 00, ngày 22/08/2023 theo lịch tuyển sinh chung của cả nước. Thí sinh tra cứu kết quả vào ngày 22/08/2023, tải giấy báo trúng tuyển nhập học và làm theo hướng dẫn ghi trong giấy báo.  - Thí sinh xác nhận nhập học trên Hệ thống Tuyển sinh Quốc gia của Bộ GD&ĐT; đồng thời, thí sinh phải đến trường làm thủ tục nhập học và đóng học phí từ ngày 22/08/2023 đến trước 17 giờ 00, ngày 06/09/2023.');
INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH122'), 'Chào bạn, khi bạn nộp hồ sơ trực tiếp tại trường, văn phòng tuyển sẽ nhận hồ sơ và kiểm duyệt, nếu hồ sơ hợp lệ trường sẽ ghi biên nhận và chờ kết quả. Trường hợp bạn gửi hồ sơ trực tuyến nếu không hợp lệ trường sẽ gửi mail để bạn bổ sung lại hồ sơ nhé.');
