INSERT INTO TraLoiTuVan (macauhoi, cautraloi) VALUES ((SELECT macauhoi FROM CauHoiTuVan WHERE macauhoi = 'CH132'), 'Chào em, Trường có 2 loại chính sách học bổng - Học bổng tài năng: + Học bổng bằng 100%, 50% học phí 4 năm học cho các thí sinh có điểm cao trúng tuyển và nhập học vào STU. + Học bổng bằng 100% học phí 4 năm học cho thí sinh đã nhận học bổng "STU - Chắp cánh ước mơ", trúng tuyển và nhập học  vàp STU. + Học bổng bằng 50% học phí 4 năm học cho thí sinh có điểm cao hoặc thủ khoa đầu vào STU trúng tuyển và nhập học theo từng phương thức. - Học bổng khuyến khích: Học bổng bằng 20% học kỳ 1 năm thứ nhất cho các thí sinh trúng tuyển và nhập học đầu tiên bậc đại học chính quy của STU. ');
